import './App.css';
import logo from './logo.png';
import React, { useState } from 'react';

function App() {
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [loginSucesso, setLoginSucesso] = useState(false);
  const [loginErro, setLoginErro] = useState(false);

  const handleLogin = () => {
    fetch('http://localhost:5000/api/login', {
      method: 'POST',
      body: JSON.stringify({ email, senha }),
      headers: { 'Content-Type': 'application/json' }
    })
    .then(response => response.json())
    .then(data => {
      if (data.success) {
        setLoginSucesso(true);
        setLoginErro(false);
        setTimeout(() => setLoginSucesso(false), 3000);
      } else {
        setLoginErro(true);
        setLoginSucesso(false);
        setSenha(''); // Limpa a senha quando estiver incorreta
        setTimeout(() => setLoginErro(false), 3000);
      }
    })
    .catch(error => {
      console.error('Erro:', error);
      setLoginErro(true);
      setSenha(''); // Limpa a senha em caso de erro
      setTimeout(() => setLoginErro(false), 3000);
    });
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleLogin();
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        {loginSucesso && (
          <div style={{
            position: 'fixed',
            top: '20px',
            right: '20px',
            background: '#4CAF50',
            color: 'white',
            padding: '15px 20px',
            borderRadius: '8px',
            fontSize: '16px',
            zIndex: 1000
          }}>
            Login realizado com sucesso!
          </div>
        )}
        {loginErro && (
          <div style={{
            position: 'fixed',
            top: '20px',
            right: '20px',
            background: '#f44336',
            color: 'white',
            padding: '15px 20px',
            borderRadius: '8px',
            fontSize: '16px',
            zIndex: 1000
          }}>
            Email ou senha incorretos!
          </div>
        )}
        <div className="login-background">
          <div className="login-container">
            <img src={logo} alt="Logo" className="logo" />
            <h1 className="titulo">T-DRAW</h1>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={e => setEmail(e.target.value)}
              onKeyPress={handleKeyPress}
              className="input"
            />
            <input
              type="password"
              placeholder="Senha"
              value={senha}
              onChange={e => setSenha(e.target.value)}
              onKeyPress={handleKeyPress}
              className="input"
            />
            <button className="entrar" onClick={handleLogin}>Entrar</button>
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
