import './App.css';
import logo from './logo.png';
import React, { useState } from 'react';

function App() {
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [logado, setLogado] = useState(false);

  const handleLogin = () => {
    fetch('http://localhost:5000/api/login', {
      method: 'POST',
      body: JSON.stringify({ email, senha }),
      headers: { 'Content-Type': 'application/json' }
    }).then(() => {
      setLogado(true);
    });
  };

  if (logado) {
    return (
      <div className="login-background">
        <div className="login-container" style={{ justifyContent: 'center', alignItems: 'center', height: '300px' }}>
          <h2 style={{ textAlign: 'center' }}>Bem-vindo! Login realizado com sucesso.</h2>
        </div>
      </div>
    );
  }

  return (
    <div className="App">
      <header className="App-header">
        <div className="login-background">
          <div className="login-container">
            <img src={logo} alt="Logo" className="logo" />
            <h1 className="titulo">T-DRAW</h1>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={e => setEmail(e.target.value)}
              className="input"
            />
            <input
              type="password"
              placeholder="Senha"
              value={senha}
              onChange={e => setSenha(e.target.value)}
              className="input"
            />
            <button className="entrar" onClick={handleLogin}>Entrar</button>
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
