/**
 * Componente de formulário de login
 * Interface simples de autenticação com campos de email e senha
 */

import React, { useState } from 'react';

/**
 * Componente que renderiza o formulário de login
 */
function LoginForm({ onLoginSuccess, onBack }) {
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  /**
   * Manipula o processo de login
   */
  const handleLogin = async () => {
    if (!email || !senha) {
      setError('Email e senha são obrigatórios');
      return;
    }

    if (!email.includes('@')) {
      setError('Por favor, digite um email válido');
      return;
    }

    setLoading(true);
    setError('');
    
    try {
      const response = await fetch('/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, senha })
      });
      
      const data = await response.json();
      
      if (data.success) {
        onLoginSuccess && onLoginSuccess();
      } else {
        setError(data.message || 'Email ou senha incorretos');
      }
    } catch (error) {
      setError('Erro de conexão com o servidor');
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !loading) {
      handleLogin();
    }
  };

  return (
    <div className="login-container">
      <img src="/logo.png" alt="T-Draw Logo" className="login-logo" />
      <h1 className="login-titulo">T-DRAW</h1>
      
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={e => setEmail(e.target.value)}
        onKeyPress={handleKeyPress}
        className="login-input"
        disabled={loading}
      />
      
      <input
        type="password"
        placeholder="Senha"
        value={senha}
        onChange={e => setSenha(e.target.value)}
        onKeyPress={handleKeyPress}
        className="login-input"
        disabled={loading}
      />
      
      <button 
        className="login-button" 
        onClick={handleLogin}
        disabled={loading}
      >
        {loading ? 'Entrando...' : 'Entrar'}
      </button>
      
      {error && (
        <div className="error-notification">
          {error}
        </div>
      )}
    </div>
  );
}

export default LoginForm;
